package Ejercicio.basico;

public class IncrementoDecremento2 extends IncrementoDecremento {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 char A = 'c';
		 char B;
		 System.out.println(A++);
		 System.out.println(A++);
		 System.out.println(++A);
		 B = --A;
		 System.out.println(++A);
		 A++;
		 --B;
		 System.out.println(B++);
		 System.out.println(++B);
		 System.out.println(++A);
		 System.out.println(B--);
		 System.out.println(A);
		 System.out.println(B);


	}

}
